export default {
  mounted() {
    function addEventListenerOnce(target, type, listener) {
      target.addEventListener(type, function fn(event) {
          target.removeEventListener(type, fn);
          listener(event);
      });
    }
    
    addEventListenerOnce(document.getElementById('content'), "scroll", function (event) {
        document.getElementById('mobile-scroll-hint').style.display = "none"
    });
  }
}
</script>

